mapper.select:
SELECT p.id,p.cate_id,p.`name`,p.price,p.stock,p.`status`,p.detail,p.create_time,p.update_time,p.img_address
FROM product p
INNER JOIN category c ON c.id=p.cate_id


        and p.cate_id=#{cateId}


        and p.`name` like concat('%',#{name},'%')


        and c.parent_id=#{parentId}


LIMIT #{currentNum},#{pageSize}

SELECT COUNT(1)
FROM product p
INNER JOIN category c ON c.id=p.cate_id


        and p.cate_id=#{cateId}


        and p.`name` like concat('%',#{name},'%')


        and c.parent_id=#{parentId}

mapper.delete:
DELETE FROM product
WHERE id in

    #{sid}
